INSERT INTO governance_documents (id, danger_bank_id, name, type, content) VALUES ('e37284e28a764b819e43cf9e7a785501', 'd3f39cd50d004416a31cb3c3d079bfd9', '需整改问题.txt', 'txt', '1.隐患列表：隐患库表格字段名称务必按照给你的Excel表格字段名称设计。
