mapper.select:
SELECT 

FROM rating a

WHERE a.id = #{id}

SELECT
FROM rating a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM rating a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rating SET 	
	name = #{name}
WHERE id = #{id}

UPDATE rating SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
